#include "../Cabeceras/Datos.h"
#include <SFML/Graphics.hpp>
#include <map>
#include <vector>
#include <string>

using namespace std;
using namespace sf;

constexpr int escalado = 15;

Datos::Datos(int x, int y): offsetX(x), offsetY(y) {
    colorMap = {
        {"c_1", Color(0, 0, 0)},
        {"c_2", Color(148, 148, 148)},
        {"c_3", Color(40, 189, 187)},
        {"c_4", Color(92, 92, 92)},
        {"c_5", Color(174, 173, 173)},
        {"c_6", Color(207, 207, 207)},
    };
}

void Datos::dibujaCuadrado(int a, int b, const string& colorRelleno, RenderWindow& window) {
    const int x = a * escalado + offsetX;
    const int y = b * escalado + offsetY;

    RectangleShape rect(Vector2f(escalado, escalado));
    rect.setPosition(x, y);
    rect.setFillColor(colorMap[colorRelleno]);

    window.draw(rect);
}

void Datos::dibujaFila(int fila, const vector<string>& colores, RenderWindow& window) {
    for (size_t i = 0; i < colores.size(); ++i) {
        if (!colores[i].empty()) {
            dibujaCuadrado(i, fila, colores[i], window);
        }
    }
}

